  render() {
if ( this.state.hasPermission && this.state.btnState === true){
  return (
   <BarCodeScanner  onBarCodeScanned = { this.state.scanned ? undefined : this.handleScan } style={StyleSheet.absoluteFillObject} /> 
  )
}
else {
  
    return (
      <View style={styles.container}>
          <View style={styles.row}>
      <Icon style={styles.icon} name="money-check" color='#7f5af0' size={27} />
        <Text style={styles.title}>Transactions</Text>
        </View>



    

        <TextInput
        placeholder="Student Id"
