          <Text>Scan Student Id </Text>
        </TouchableOpacity>

      


      

      <TextInput
        placeholder="Book Id"
        keyboardAppearance="dark"
        style={styles.input}
        value={this.state.scannedBookId}
      ></TextInput>


      <TouchableOpacity style={styles.scan} onPress={this.getCamPermissions(bookId)}>
          <Text>Scan Book Id </Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.scan} onPress={this.handleTransaction()}>
          <Text>Submit </Text>
        </TouchableOpacity>
      
      </View>
    );

}
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
